SELECT (str(?seriesIdValue) as ?seriesId) (str(?hasPartValue) as ?hasPart) FROM <$GRAPH_NAME> WHERE {
?resourceMap dcterms:isPartOf ?project . ?project dcterms:identifier ?seriesIdValue . ?resourceMap dcterms:identifier ?hasPartValue .
}

PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX ore: <http://www.openarchives.org/ore/terms/> PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT (str(?pidValue) as ?pid) (str(?isPartOfValue) as ?isPartOf) FROM <$GRAPH_NAME> WHERE {
?project dcterms:hasPart ?resourceMap . ?resourceMap dcterms:identifier ?pidValue . ?project dcterms:identifier ?isPartOfValue .
