constructor-arg.value:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX oboe-core: <http://ecoinformatics.org/oboe/oboe.1.0/oboe-core.owl#> PREFIX oa: <http://www.w3.org/ns/oa#> PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT ?standard_sm ?pid FROM <$GRAPH_NAME> WHERE {
?measurement rdf:type oboe-core:Measurement . ?measurement rdf:type ?restriction . ?restriction owl:onProperty oboe-core:usesStandard . ?restriction owl:allValuesFrom ?standard . ?standard rdfs:subClassOf+ ?standard_sm . ?standard_sm rdfs:subClassOf oboe-core:Standard .
?annotation oa:hasBody ?measurement . ?annotation oa:hasTarget ?target . ?target oa:hasSource ?metadata . ?metadata dcterms:identifier ?pid .
}

PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX oboe-core: <http://ecoinformatics.org/oboe/oboe.1.0/oboe-core.owl#> PREFIX oa: <http://www.w3.org/ns/oa#> PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT ?characteristic_sm ?pid FROM <$GRAPH_NAME> WHERE {
?measurement rdf:type oboe-core:Measurement . ?measurement rdf:type ?restriction . ?restriction owl:onProperty oboe-core:ofCharacteristic . ?restriction owl:allValuesFrom ?characteristic . ?characteristic rdfs:subClassOf+ ?characteristic_sm . ?characteristic_sm rdfs:subClassOf oboe-core:Characteristic .
?annotation oa:hasBody ?measurement . ?annotation oa:hasTarget ?target . ?target oa:hasSource ?metadata . ?metadata dcterms:identifier ?pid .
}

PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX oa: <http://www.w3.org/ns/oa#> PREFIX dcterms: <http://purl.org/dc/terms/> PREFIX prov: <http://www.w3.org/ns/prov#> PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT ?creator_sm ?pid FROM <$GRAPH_NAME> WHERE {
?metadata dcterms:identifier ?pid . ?metadata prov:wasAttributedTo ?person . ?person dcterms:identifier ?creator_sm .
}
